INSERT INTO `boxes` VALUES (11,'<DIV ALIGN=CENTER><h2><a href=\"?q=logout\">Logout</a></h2></div>','Logout',2),(16,'<a href=\"?=content/about-easymap\">About EasyMap</a>','about easymap',1),(8,'<br>\r\n<a href=\"?q=content/map-data\" target=\"_blank\">Create New Map for this project</a>\r\n','Create New Map',1),(5,'<p style=\"text-align: center;\"><a href=\"?q=who\">Who Did This?</a></p>','Who Did This?',1),(7,'<br>','Create New Project',1),(20,'<div align=center><h2><b><a href=\"/easymap/content/request-ourmap-account\" TARGET=\"_blank\">REQUEST AN OURMAP ACCOUNT</a></b></h2></div>','Request User Account',2),(15,'<a href=\"?=content/developers-area\">Developer\'s Area</a>','developer\'s area',1),(14,'<a href=\"http://www.artmobilelab.ca/EasyMapWeb/listusers.do\" target=\"_blank\">Create New Map For This Project</a>','new map',1),(17,'<script type=\"text/javascript\" src=\"/ajax.js\" ></script>\r\n\r\n<script type=\"text/javascript\" >\r\n\r\n   var mapTab = null;\r\n\r\n  function onTabClose() {\r\n      closeWin();\r\n  }\r\n\r\n  function closeWin() {\r\n     if ( mapTab != null ) \r\n        mapTab.close();\r\n     mapTab = null;\r\n  }\r\n\r\n  \r\n\r\n  function openWin( url ) {\r\n   \r\n     if ( mapTab != null && mapTab.closed ) \r\n          mapTab = null;\r\n\r\n     if ( mapTab == null )\r\n     {\r\n	mapTab = window.open( url,\'map_popup\', \'resizable=yes,menubar=no,location=no,toolbar=no,status=no,scrollbars=yes,directories=no,width=550,height=430,left=80,top=50\');\r\n     }\r\n\r\n     mapTab.location.href = url ;\r\n     mapTab.focus();\r\n\r\n  }\r\n\r\n  window.onunload = onTabClose;\r\n\r\n</script>\r\n\r\n<?php\r\n\r\nglobal $user;\r\n$uid = $user->uid;\r\n\r\n$debug = false ;\r\n\r\n$result = mysql_query(\"SELECT u.pass,u.uid,u.name, r.rid as rol FROM users u, users_roles r WHERE u.uid = $uid and r.uid = u.uid\");\r\n$namepass = mysql_fetch_array( $result );\r\n\r\n//User\'s status:\r\n\r\n$isOverlord = false;\r\n$isCreator   = false;\r\n$isOwner     = false;\r\n$isMember   = false;\r\n$mapPrivacy = 0 ;\r\n$isProjectOwner   = false;\r\n$isProjectMember  = false;\r\n$isProjectCreator = false;\r\n$notEvenAProjectMember = false;\r\n\r\n// Check if user is overlord\r\nif ( $namepass[\'rol\'] == 6 )\r\n   $isOverlord = true ;\r\n\r\n   $isOverlordStr = ( $isOverlord ) ? \"Yes\" : \"No\" ;\r\n\r\n\r\nif (arg(0) == \'node\')\r\n{\r\n\r\n   global $map_db;\r\n   global $map_host;\r\n   global $map_port;\r\n   global $map_user;\r\n   global $map_pwd;\r\n\r\n   $dbconn = pg_connect(\"dbname= \" . $map_db . \" host=\" . $map_host . \" port=\" . $map_port . \" user=\" . $map_user . \" password=\" . $map_pwd );\r\n\r\n   $project_id = arg(1);\r\n   $sqlstr = \"select mapname, mapid, privacy, created_by from public.maps where project_id=\".$project_id.\" order by created\";\r\n   $result = pg_query($dbconn, $sqlstr );\r\n   $counter = 0;\r\n\r\n\r\n   // Check if the user is a member, owner or creator of the project\r\n   if ( ! $isOverlord )\r\n   {\r\n\r\n\r\n      //Check project owner\r\n      $sqlStmt = \"select nid, field_makeowner_uid as uid from content_field_makeowner where nid = \" . $project_id . \" and field_makeowner_uid = \" . $uid ;\r\n      $projectOwnersResult = mysql_query($sqlStmt) ;\r\n      if ( $projectOwnersResult )\r\n          if ( mysql_num_rows( $projectOwnersResult ) > 0 )\r\n              $isProjectOwner = true ;\r\n\r\n     // Check project member\r\n     $sqlStmt = \"select nid, field_reference_uid as uid from content_field_reference where nid = \" . $project_id . \" and field_reference_uid = \" . $uid ;\r\n     $projectMembersResult = mysql_query($sqlStmt) ;\r\n     if ( $projectMembersResult )\r\n         if ( mysql_num_rows($projectMembersResult) > 0 )\r\n            $isProjectMember = true;\r\n\r\n\r\n      // Get project creator\r\n      $sqlStmt = \"select uid from node where nid = \" . $project_id ;\r\n      $projectCreatorResult = mysql_query($sqlStmt);\r\n      $projectCreatorRo