erid = \'$uid\' and p.element_id = \" . $row[1] . \"and p.element_type = 1\" ;\r\n      $checkPermissionArray = pg_query($dbconn,$checkPermissionSql) ;\r\n      $checkPermissionRow = pg_fetch_row($checkPermissionArray) ;\r\n\r\n\r\n      $isOwner = false ;\r\n      $isMember = false ;\r\n      if ( $checkPermissionRow )\r\n      {\r\n         if ( $checkPermissionRow[0] == 1 )\r\n             $isOwner = true;\r\n         if ( $checkPermissionRow[1] == 1 )\r\n             $isMember = true;\r\n      }\r\n\r\n      // Check if the user has permission to see the map\r\n      $hasPermission = false ;\r\n\r\n      // if user is overlord (admin), then it always has permission\r\n      // or the creator of this project, (but not co-owners)\r\n      if ( $isOverlord || $isProjectCreator )\r\n      {\r\n         $hasPermission = true;\r\n      }\r\n      else if ( $notEvenAProjectMember )\r\n      {\r\n         if ( $mapPrivacy == 2 )\r\n            $hasPermission = true ;\r\n      }\r\n      else\r\n      {\r\n         if ( $mapPrivacy == 0 )\r\n            $hasPermission = $isCreator | $isOwner ;\r\n         if ( $mapPrivacy == 1 )\r\n            $hasPermission = $isCreator | $isOwner | $isMember ;\r\n         if ( $mapPrivacy == 2 )\r\n            $hasPermission = true ;\r\n      }\r\n\r\n      $auserid = $namepass[\'uid\'] ;\r\n      if ( $notEvenAProjectMember )\r\n          $auserid = \"100000099\" ;\r\n\r\n      echo \"<p>\";\r\n\r\n      if ( $hasPermission )\r\n      {\r\n\r\n         $mapUrl = \"http://ourmapmaker.ca:8080/map/viewmap.do?mapid=\" . $row[1]  . \r\n                          \"&auserid=\" . $auserid . \r\n                          \"&user=\" . $namepass[\'name\'] . \r\n                          \"&apwd=\" . $namepass[\'pass\'] ; \r\n\r\n         echo  \"<a href=\'\" . $mapUrl . \"\' target=\'_map\" . $row[1] . \"\'  >\"  .  $row[0]  .   \"</a>\"  ;\r\n\r\n      }\r\n      else\r\n      {\r\n         echo  $row[0] ;\r\n      }\r\n\r\n      echo \" <br/> 