INSERT INTO `content_group` VALUES ('standard','profile','group_lessonplans','Upload Lesson Plans to Share with Other Teachers','a:2:{s:4:\"form\";a:2:{s:5:\"style\";s:18:\"fieldset_collapsed\";s:11:\"description\";s:550:\"Use this tool to upload your Lesson Plans and other useful mapping project resources to share with other teachers using this site. Files uploaded here will be published on the Shared Lesson Plans page in the Curriculum section, where other users can then view and download them. (These file links will also be visible to you on your profile page.) Once your file is uploaded, make sure that you click the \"SAVE\" button at the bottom of this page to finalize the upload - otherwise your file will NOT be saved or added to the Shared Lesson Plans page.\";}s:7:\"display\";a:7:{s:11:\"description\";s:0:\"\";s:6:\"teaser\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:4:\"full\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:4;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:5;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"token\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"label\";s:5:\"above\";}}',15),('standard','project','group_member','Add/Remove Project Members','a:2:{s:4:\"form\";a:2:{s:5:\"style\";s:18:\"fieldset_collapsed\";s:11:\"description\";s:1075:\"The following site users have requested to join this project via the JOIN PROJECTS page. To approve their requests, just check off the box to the left of each user\'s name. (If you aren\'t sure about an individual user, you can click on their name to see their profile page before approving their request.) \r\nDon\'t forget to click the SAVE button at the bottom of this page once you\'ve made these changes! When you save the page, all \"checked\" users will become project Members. You can add or remove project Members at any time, simply by checking or un-checking their boxes. Members will be able to access this Project page as a direct link from their profile page (unless the Security Setting is set to \"Private\"). And when you create maps for this project, these Member users will also be eligible to become Members of each individual map as well (membership to maps is not automatic - you will have to choose which Project members should also be members of each map, in a separate step). Members of a project cannot create maps - only Owners and Co-Owners may create maps.\";}s:7:\"display\";a:7:{s:11:\"description\";s:0:\"\";s:6:\"teaser\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:4:\"full\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:4;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:5;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"token\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"label\";s:5:\"above\";}}',31),('standard','project','group_coowner','Add/Remove Project Co-owners','a:2:{s:4:\"form\";a:2:{s:5:\"style\";s:18:\"fieldset_collapsed\";s:11:\"description\";s:705:\"Use this tool if you want to make one or more other users Co-Owners of this project. Add or remove Co-Owners at any time by checking/un-checking the boxes beside the user names and then saving your changes. Co-Owners will have most of the same editing powers as the Owner of this project page: They will be able to edit the details on this project page, but they will not be allowed to delete the project. Co-Owners will also be allowed to create new maps for this project (unlike Members), and are eligible to become Members of maps created by others. However, they will not be able to edit or delete maps created by other Owners/Co-Owners, unless they are granted Co-Owner status for those maps as well.\";}s:7:\"display\";a:7:{s:11:\"description\";s:0:\"\";s:6:\"teaser\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:4:\"full\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:4;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:5;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"token\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"label\";s:5:\"above\";}}',32),('standard','project','group_files','Upload Files to this Project','a:2:{s:4:\"form\";a:2:{s:5:\"style\";s:18:\"fieldset_collapsed\";s:11:\"description\";s:308:\"Use this tool to upload files and make them available to the users of this project. Below is a  \"browse\" button for uploading a file from your computer. You can upload as many files as you like - to add more files, just hit the \"Add another item\" button to create a new slot. You can upload any type of file.\";}s:7:\"display\";a:7:{s:11:\"description\";s:0:\"\";s:6:\"teaser\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:4:\"full\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:4;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}i:5;a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"token\";a:2:{s:6:\"format\";s:8:\"fieldset\";s:7:\"exclude\";i:0;}s:5:\"label\";s:5:\"above\";}}',30);
