INSERT INTO `node_type` VALUES ('page','Page','node','A <em>page</em>, similar in form to a <em>story</em>, is a simple method for creating and displaying information that rarely changes, such as an \"About us\" section of a website. By default, a <em>page</em> entry does not allow visitor comments and is not featured on the site\'s initial home page.','',1,'Title',1,'Body',0,1,1,0,'page'),('story','Story','node','A <em>story</em>, similar in form to a <em>page</em>, is ideal for creating and displaying content that informs or engages website visitors. Press releases, site announcements, and informal blog-like entries may all be created with a <em>story</em> entry. By default, a <em>story</em> entry is automatically featured on the site\'s initial home page, and provides the ability to post comments.','',1,'Title',1,'Body',0,1,1,0,'story'),('book','Book','node','A <em>book page</em> is a page of content, organized into a collection of related entries collectively known as a <em>book</em>. A <em>book page</em> automatically displays links to adjacent pages, providing a simple navigation system for organizing and reviewing structured content.','',1,'Book Name',0,'',0,1,1,0,'book'),('poll','Poll','poll','A <em>poll</em> is a question with a set of possible responses. A <em>poll</em>, once created, automatically provides a simple running count of the number of votes received for each response.','',1,'Question',0,'',0,0,0,1,'poll'),('date','Date','node','A date content type that is linked to a Views calendar.','',1,'Title',1,'Body',0,1,1,0,'date'),('profile','Profile','node','A user profile built as content.','',1,'Title',0,'',0,1,1,0,'profile'),('project','Project','node','Creates a new project','',1,'Project Name',0,'',0,1,1,0,''),('blog','Blog entry','blog','A <em>blog entry</em> is a single post to an online journal, or <em>blog</em>.','',1,'Title',1,'Body',0,0,1,1,'blog'),('student_profile','Student Profile','node','Profile page for Students','',1,'Title',0,'',0,1,1,0,''),('forum','Forum topic','forum','A <em>forum topic</em> is the initial post to a new discussion thread within a forum.','',1,'Subject',1,'Body',0,0,1,1,'forum'),('panel','Panel','panels_node','A panel layout broken up into rows and columns.','',1,'Title',1,'Teaser',0,0,0,1,'panel'),('general','General Content','node','Used for basic informative content pages','',1,'Title',1,'Body',0,1,1,0,''),('feed','Teacher Info','node','Site admins can use this to get information to teachers','',1,'Title',1,'Body',0,1,1,0,'');
